import {

} from "./../types/categories";

const initialState = {
  data: []  
};

let reducer = (state = initialState, action) => {
  switch (action.type) {
    //ADD
    case CATE_ADD_REQUEST:
      return { ...state };

    case CATE_ADD_SUCCESS:
      return {
        ...state,
      };
    //GET API
    case CATE_GET_SUCCESS:
      return {
        ...state,
        data: action.payload,
      };
    //DELETE
    case CATE_DELETE_SUCCESS:
      return {
        ...state,
        data: state.data.filter((e) => e.id !== action.payloadid),
      };
    //SUBMIT
    case CATE_SUBMIT_SUCCESS:
      console.log(action.data)
      return {
        ...state,
        data:[...state.data,action.payload]


      };
      
    
      //UPDATE_EDIT 

      case CATE_UPDATE_EDIT_SUCCESS:
      var index = -1
      index = findID(state.data,action.payload.id); // so sánh nè
      state.data[index] = action.payload;
      console.log("API: ",action.payload)
      console.log("Store: ",state.data[index])
      console.log("State: ",state)
      return {
        ...state,
        data: [...state.data]

      };

    default:
      break;
  }
  return state;
};

export default reducer;
