import { takeLatest, put, all } from "redux-saga/effects";
import axios from "axios";

import {
    PRODUCT_CREATE_REQUEST,
    PRODUCT_CREATE_SUCCESS
} from "./../types/products";

// lấy tất cả data từ products

function* createProduct({data}) {
console.log(data)
  const res = yield axios.post("http://localhost:3000/api/products", data);
  console.log(res)
  if (res.status === 200) {
    const { data } = res.data;
    yield put({ type: PRODUCT_CREATE_SUCCESS, payload: data });
  }
}

function* createProductRequest() {
  yield takeLatest(PRODUCT_CREATE_REQUEST, createProduct);
}

export default function* () {
  yield all([createProductRequest()]);
}
